document.addEventListener("DOMContentLoaded",()=>{const $navbarBurgers=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0);const $menu=document.querySelector(".navbar-burger.game-menu");const $Resetter=document.querySelector(".navbar-item.ResetGame");$navbarBurgers.forEach(el=>{el.addEventListener("click",()=>{const target=el.dataset.target;const $target=document.getElementById(target);el.classList.toggle("is-active");$target.classList.toggle("is-active")})});if($Resetter!==undefined&&$Resetter!==null){$Resetter.addEventListener("click",function(){const target=$menu.dataset.target;const $target=document.getElementById(target);$target.classList.remove("is-active");$navbarBurgers.forEach(el=>{el.classList.remove("is-active")})})}});